import java.util.HashSet;
import java.util.Set;

public class Dec06 {

    public static void main(String[] args) {
        String input = "";
        char[] characterArray = input.toCharArray();
        System.out.println("Number of characters processed for marker: " + getNumCharsProcessed(characterArray, 4));
        System.out.println("Number of characters processed for message: " + getNumCharsProcessed(characterArray, 14));
    }

    public static int getNumCharsProcessed(char[] characters, int limit) {
        LimitedSizeLinkedList<Character> characterBuffer = new LimitedSizeLinkedList<>(limit);
        for (int i = 0; i < characters.length; i++) {
            characterBuffer.add(characters[i]);
            if (!characterBuffer.isAtLimit()) {
                continue;
            }
            Set<Character> characterSet = new HashSet<>(characterBuffer);
            if (characterSet.size() == characterBuffer.getSizeLimit()) {
                return i + 1;
            }
        }
        // Error case
        return 0;
    }
}
